import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";

import { useLocation } from "react-router-dom";

function App() {
  const [data, setData] = useState(null);
  const [num, setNum] = useState(0);
  const [dataShown, setdataShown] = useState(null);
  const [id, setId] = useState(null);

  const s = useLocation().search;
  const m = getQueryParams(s);

  const nums = [1, 2, 3, 4, 5];

  useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => (setData(data.message), setId(data.id)));
  }, []);

  console.log(id);
  useEffect(() => {
    let length = 0;
    setTimeout(() => {
      console.log(data);
      if (data) {
        length = data.length;
        console.log(length);
      } else {
        length = 3;
      }
      setNum((num + 1) % length);
      setdataShown(data[num]);
    }, 3000);
  }, [num]);

  return (
    <>
      <div className="App">
        <header className="App-header">
          <h1>{!dataShown ? "Loading name..." : `${dataShown.name}`} </h1>
          <p>
            {!dataShown ? "Loading description..." : `${dataShown.description}`}
          </p>

          <img
            className="photo"
            src={!dataShown ? null : `${dataShown.image}`}
          />
        </header>
      </div>
    </>
  );
}

function Home() {
  return (
    <div>
      <h2>Home</h2>
    </div>
  );
}

function About() {
  return (
    <div>
      <h2>About</h2>
    </div>
  );
}

function Dashboard() {
  return (
    <div>
      <h2>Dashboard</h2>
    </div>
  );
}

export default App;
