import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

export default class CreateAd extends Component {
  constructor(props) {
    super(props);

    // Setting up functions
    this.onChangeAdName = this.onChangeAdName.bind(this);
    this.onChangeAdDescription = this.onChangeAdDescription.bind(this);
    this.onChangeAdImage = this.onChangeAdImage.bind(this);
    this.onChangeAdScreens = this.onChangeAdScreens.bind(this);

    this.onSubmit = this.onSubmit.bind(this);

    // Setting up state
    this.state = {
      name: "",
      description: "",
      image: "",
      screens: "",
    };
  }

  onChangeAdName(e) {
    this.setState({ name: e.target.value });
  }

  onChangeAdDescription(e) {
    this.setState({ description: e.target.value });
  }

  onChangeAdImage(e) {
    this.setState({ image: e.target.value });
  }

  onChangeAdScreens(e) {
    this.setState({ screens: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();

    console.log(`Ad successfully created!`);
    console.log(`Name: ${this.state.name}`);
    console.log(`Description: ${this.state.description}`);
    console.log(`Image URL: ${this.state.image}`);
    console.log(`Screens: ${this.state.screens}`);

    this.setState({ name: "", description: "", image: "", screens: "" });
  }

  render() {
    return (
      <div className="form-wrapper">
        <Form onSubmit={this.onSubmit}>
          <Form.Group controlId="Name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={this.state.name}
              onChange={this.onChangeAdName}
            />
          </Form.Group>

          <Form.Group controlId="Description">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="text"
              value={this.state.description}
              onChange={this.onChangeAdDescription}
            />
          </Form.Group>

          <Form.Group controlId="Image">
            <Form.Label>Image URL</Form.Label>
            <Form.Control
              type="text"
              value={this.state.image}
              onChange={this.onChangeAdImage}
            />
          </Form.Group>

          <Form.Group controlId="Screens">
            <Form.Label>Screens</Form.Label>
            <Form.Control
              type="text"
              value={this.state.screens}
              onChange={this.onChangeAdScreens}
            />
          </Form.Group>

          <Button variant="danger" size="lg" block="block" type="submit">
            Create Ad
          </Button>
        </Form>
      </div>
    );
  }
}
