import React from "react";
import { useParams } from "react-router-dom";
import "./EditAd.css";
import { useState, useEffect } from "react";
import axios from "axios";

function EditAd() {
  const params = useParams();
  const [ads, setAds] = useState([]);
  const [currentAd, setCurrentAd] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/ad/`)
      .then((res) => {
        setAds(res.data.filter((ad) => ad._id == `${params.id}`));
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  //   let filterAd = ads.filter((ad) => ad._id == `${params.id}`);

  console.log(ads);

  return (
    <form className="form">
      <h1>{ads[0] ? "loading" : ads[0].name}</h1>
      <div className="myinput">
        <label>Id:</label>
        <input type="text" placeholder={params.id} />
      </div>
      <div className="myinput">
        <label>Title</label>
        <input
          type="text"
          //   placeholder={filterAd ? "loading" : filterAd[0].name}
        />
      </div>
      <div className="myinput">
        <label>Description</label>
        <input type="text" placeholder={params.id} />
      </div>
    </form>
  );
}

export default EditAd;
