import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [data, setData] = useState(null);
  const [test, setTest] = useState(null);

  useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);

  var flag = true;

  setInterval(() => {
    if (flag) {
      setTest(data);
      flag = false;
    } else setTest(null);
  }, 2000);

  useEffect(() => {
    console.log(test);
  }, test);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

        <p>{!data && test ? "Loading..." : `${data[0].image + " " + test}`}</p>
        <img src={data[0].image} />
      </header>
    </div>
  );
}

export default App;
