import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import { useEffect } from "react";

import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
} from "react-router-dom";
import Home from "./Home.js";
import ShowAllAds from "./components/ShowAllAds";
import EditAd from "./components/EditAd";
import CreateAd from "./components/CreateAd";
import MediaCard from "./components/MediaCard";
import Welcome from "./components/Welcome";
import "bootstrap/dist/css/bootstrap.min.css";
import reportWebVitals from "./reportWebVitals";
import ViewAds from "./components/ViewAds";
import { useState } from "react";
import { useLocation } from "react-router-dom";
// import SignIn from "./components/SignIn";
import { Navigate } from "react-router-dom";

const SERVER = "http://localhost:8000/";

function App() {
  let location = useLocation();
  const [isAdmin, setIsAdmin] = useState(false);
  // console.log(location);

  function SignIn(props) {
    const [userData, setUserData] = useState({
      username: "",
      password: "",
    });
    const [errorMessage, setErrorMessage] = useState("");
    const navigate = useNavigate();

    function handleChange(e) {
      const { name, value } = e.target;
      setUserData((prevState) => ({
        ...prevState,
        [name]: value,
      }));
      // console.log(userData);
      console.log("IS ADMIN=" + isAdmin);
    }

    function handleSubmit(e) {
      e.preventDefault();
      // console.log("submitted" + userData.username + userData.password);
      // console.log(localStorage.getItem("isAuthenticated"));
      if (userData.username === "" || userData.password === "") {
        setErrorMessage((prevState) => ({
          value: "Empty username/password field",
        }));
      } else if (
        userData.username.toLowerCase() === "admin" &&
        userData.password === "123456"
      ) {
        //Signin Success
        // localStorage.setItem("isAuthenticated", "true");
        // window.location.pathname = "/screen=0";
        console.log("success");
        // console.log(localStorage.getItem("isAuthenticated") + "SIGNIN PAGE");
        setIsAdmin(true);
      } else {
        //If credentials entered is invalid
        setErrorMessage((prevState) => ({
          value: "Invalid username/password",
        }));
        return;
      }
    }
    return (
      <div>
        <form>
          <h1>Sign-In</h1>
          <input
            name="username"
            placeholder="username"
            onChange={handleChange}
          ></input>
          <input
            name="password"
            placeholder="password"
            onChange={handleChange}
          ></input>
          <button onClick={handleSubmit}>Submit</button>
        </form>
      </div>
    );
  }

  if (
    (location.pathname === "/screen=0" ||
      location.pathname === "/" ||
      location.pathname === "/allads" ||
      location.pathname === "/edit-ad/:id" ||
      location.pathname === "/create-ad") &&
    !isAdmin
  ) {
    return (
      <div>
        <Home />
        <SignIn />
      </div>
    );
  }
  if (
    (location.pathname === "/screen=0" ||
      location.pathname === "/" ||
      location.pathname === "/allads" ||
      location.pathname === "/edit-ad/:id" ||
      location.pathname === "/create-ad") &&
    isAdmin
  ) {
    return (
      <div>
        <Home />
      </div>
    );
  } else {
    return (
      <>
        {/* <Router> */}
        <Home />
        <Routes>
          <Route
            path="/screen=0"
            element={
              // <PrivateRoute>
              <Welcome />
              // </PrivateRoute>
            }
          />

          <Route path="/screen=:id" element={<ViewAds />} />
          <Route path="/allads" element={<ShowAllAds />} />
          <Route path="/edit-ad/:id" element={<EditAd />} />
          <Route path="/create-ad" element={<CreateAd />} />
          <Route path="/sign-in" element={<SignIn />} />
        </Routes>
        {/* </Router> */}
      </>
    );
  }
}

export default App;
