import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

export default class CreateAd extends Component {
  constructor(props) {
    super(props);

    // Setting up functions
    this.onChangeAdName = this.onChangeAdName.bind(this);
    this.onChangeAdDescription = this.onChangeAdDescription.bind(this);
    this.onChangeAdImage = this.onChangeAdImage.bind(this);
    this.onChangeAdScreens = this.onChangeAdScreens.bind(this);

    this.onSubmit = this.onSubmit.bind(this);

    // Setting up state
    this.state = {
      name: "",
      description: "",
      image: "",
      screens: "",
    };
  }

  onChangeAdName(e) {
    this.setState({ name: e.target.value });
  }

  onChangeAdDescription(e) {
    this.setState({ description: e.target.value });
  }

  onChangeAdImage(e) {
    this.setState({ image: e.target.value });
  }

  onChangeAdScreens(e) {
    this.setState({ screens: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();

    console.log(`Student successfully created!`);
    console.log(`Name: ${this.state.name}`);
    console.log(`Email: ${this.state.email}`);
    console.log(`Roll no: ${this.state.rollno}`);

    this.setState({ name: "", email: "", rollno: "" });
  }

  render() {
    return (
      <div className="form-wrapper">
        <Form onSubmit={this.onSubmit}>
          <Form.Group controlId="Name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={this.state.name}
              onChange={this.onChangeStudentName}
            />
          </Form.Group>

          <Form.Group controlId="Email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              value={this.state.email}
              onChange={this.onChangeStudentEmail}
            />
          </Form.Group>

          <Form.Group controlId="Name">
            <Form.Label>Roll No</Form.Label>
            <Form.Control
              type="text"
              value={this.state.rollno}
              onChange={this.onChangeStudentRollno}
            />
          </Form.Group>

          <Button variant="danger" size="lg" block="block" type="submit">
            Create Student
          </Button>
        </Form>
      </div>
    );
  }
}
