import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";
import {
  Container,
  Navbar,
  Nav,
  NavItem,
  NavDropdown,
  MenuItem,
} from "react-bootstrap";

function Home(props) {
  let location = useLocation();
  console.log(location);
  if (
    location.pathname == "/screen=0" ||
    location.pathname == "/allads" ||
    location.pathname == "/creat-ad" ||
    location.pathname == "/edit-ad/:id"
  ) {
    return (
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand href="/allads">All Ads</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="/create-ad">Create new Ad</Nav.Link>
              <NavDropdown title="Screens" id="basic-nav-dropdown">
                <NavDropdown.Item href="/screen=1">Screen 1</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="/screen=2">Screen 2</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="/screen=3">Screen 3</NavDropdown.Item>
              </NavDropdown>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    );
  } else {
    return null;
  }
}

export default Home;
