import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { BrowserRouter, Route, Routes, Link } from "react-router-dom";

function AdCard({ ad }) {
  return <li>{ad.name}</li>;
}

function ShowAllAds() {
  const [ads, setAds] = useState([]);
  useEffect(() => {
    axios
      .get("http://localhost:8000/api/ad")
      .then((res) => {
        setAds(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  let list = ads.map((ad) => {
    <li>{ad.name}</li>;
  });
  console.log(ads);

  return (
    <>
      <h1>{!ads ? "loading" : ads.length}</h1>
      <ul>
        {!ads
          ? "Loading Ads..."
          : ads.map((ad, index) => {
              <AdCard ad={ads} />;
            })}
      </ul>
    </>
  );
}

export default ShowAllAds;
