import { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useHistory, useParams } from "react-router-dom";
import axios from "axios";
import "./ViewAds.css";
import { useLocation } from "react-router-dom";
import socketClient from "socket.io-client";
import { createContext } from "react";

const SERVER = "http://localhost:8000/";

const Clients = createContext();

function ViewAds() {
  const [data, setData] = useState(null);
  const [num, setNum] = useState(0);
  const [dataShown, setdataShown] = useState(null);
  let location = useLocation();
  var socket = socketClient(SERVER);
  const [amount, setAmount] = useState(0);

  useEffect(() => {
    axios
      .get("http://localhost:8000/api/ad")
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  socket.on("amount", (amount, array) => {
    console.log(amount + " " + array);
    setAmount(amount);
  });

  useEffect(() => {
    // console.log(location.pathname);
    socket.emit("screenConnect", location.pathname);
  }, [location.pathname]);

  let relevantAds = [];
  const { id } = useParams();
  if (data) {
    relevantAds = data.filter((a) => a.screens.includes(id % 4));
  }

  useEffect(() => {
    let length = 0;
    setTimeout(() => {
      if (data) {
        length = relevantAds.length;
      } else {
        length = 3;
      }
      setNum((num + 1) % length);
      setdataShown(relevantAds[num]);
      // console.log(dataShown);
    }, 3000);
  }, [num]);

  return (
    <>
      <div className="App">
        <header className="App-header">
          <h4>{`Screen #${id}`}</h4>
          <h4>
            {!dataShown
              ? "Loading duration of first ad..."
              : ` duration: ${dataShown.duration}`}
          </h4>
          <h1>{!dataShown ? "Loading ad title..." : `${dataShown.name}`} </h1>
          <p>
            {!dataShown ? "Loading description..." : `${dataShown.description}`}
          </p>

          <img
            className="photo"
            src={!dataShown ? null : `${dataShown.image}`}
          />
        </header>
      </div>
      <Clients.Provider value={amount}> </Clients.Provider>
    </>
  );
}

export default ViewAds;
export { Clients };
console.log(Clients);
