import React from "react";
import { useParams } from "react-router-dom";
import "./EditAd.css";
import { useState, useEffect } from "react";
import axios from "axios";

function EditAd() {
  const params = useParams();
  const [ads, setAds] = useState([]);
  const [currentAd, setCurrentAd] = useState([]);
  const [data, setData] = useState({
    name: "",
    description: "",
    image: "",
    screens: "",
  });

  function handleChange(e) {
    setData((data) => ({ ...data, [e.target.name]: e.target.value }));
    console.log(data);
  }

  useEffect(() => {
    axios
      .get(`http://localhost:8000/api/ad/`)
      .then((res) => {
        setAds(res.data.filter((ad) => ad._id == `${params.id}`));
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  let filterAd = ads.filter((ad) => ad._id == `${params.id}`);

  console.log(ads);

  return (
    <form className="form">
      <h1>{!ads[0] ? "loading" : ads[0].name}</h1>
      <div className="myinput">
        <label>Id:</label>
        <input type="text" placeholder={params.id} disabled />
      </div>
      <div className="myinput">
        <label>Title</label>
        <input
          onChange={handleChange}
          type="text"
          name="title"
          placeholder={!ads[0] ? "loading" : ads[0].name}
        />
      </div>
      <div className="myinput">
        <label>Description</label>
        <input
          onChange={handleChange}
          type="text"
          placeholder={!ads[0] ? "loading" : ads[0].description}
        />
      </div>
      <div className="myinput">
        <label>Image URL</label>
        <input
          onChange={handleChange}
          type="text"
          placeholder={!ads[0] ? "loading" : ads[0].image}
        />
      </div>
      <div className="myinput">
        <label>Screens</label>
        <input
          onChange={handleChange}
          type="text"
          placeholder={!ads[0] ? "loading" : ads[0].screens}
        />
      </div>

      <button type="submit">Submit</button>
    </form>
  );
}

export default EditAd;
