import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import { useEffect } from "react";

import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Home from "./Home.js";
import ShowAllAds from "./components/ShowAllAds";
import EditAd from "./components/EditAd";
import CreateAd from "./components/CreateAd";
import MediaCard from "./components/MediaCard";
import Welcome from "./components/Welcome";
import "bootstrap/dist/css/bootstrap.min.css";
import reportWebVitals from "./reportWebVitals";
import ViewAds from "./components/ViewAds";
import { useState } from "react";
import { useLocation } from "react-router-dom";
import SignIn from "./components/SignIn";
import ProtectedRoute from "./components/ProtectedRoute";

const SERVER = "http://localhost:8000/";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  function SignIn(props) {
    const [userData, setUserData] = useState({
      username: "",
      password: "",
    });
    const [errorMessage, setErrorMessage] = useState("");
    const navigate = useNavigate();

    function handleChange(e) {
      const { name, value } = e.target;
      setUserData((prevState) => ({
        ...prevState,
        [name]: value,
      }));
      // console.log(userData);
    }

    function handleSubmit(e) {
      e.preventDefault();
      console.log("submitted" + userData.username + userData.password);
      console.log(localStorage.getItem("isAuthenticated"));
      if (userData.username === "" || userData.password === "") {
        setErrorMessage((prevState) => ({
          value: "Empty username/password field",
        }));
      } else if (
        userData.username.toLowerCase() === "admin" &&
        userData.password === "123456"
      ) {
        //Signin Success
        localStorage.setItem("isAuthenticated", "true");
        window.location.pathname = "/screen=0";
        console.log(localStorage.getItem("isAuthenticated") + "SIGNIN PAGE");
      } else {
        //If credentials entered is invalid
        setErrorMessage((prevState) => ({
          value: "Invalid username/password",
        }));
        return;
      }
    }
    return (
      <div>
        <form>
          <h1>Sign-In</h1>
          <input
            name="username"
            placeholder="username"
            onChange={handleChange}
          ></input>
          <input
            name="password"
            placeholder="password"
            onChange={handleChange}
          ></input>
          <button onClick={handleSubmit}>Submit</button>
        </form>
      </div>
    );
  }

  return (
    <>
      {/* <Router> */}
      <Home />
      <Routes>
        <Route path="/screen=0" element={<Welcome />} />

        <Route path="/screen=:id" element={<ViewAds />} />
        <Route path="/allads" element={<ShowAllAds />} />
        <Route path="/edit-ad/:id" element={<EditAd />} />
        <Route path="/create-ad" element={<CreateAd />} />
        <Route path="/sign-in" element={<SignIn />} />
      </Routes>
      {/* </Router> */}
    </>
  );
}

export default App;
