import React, { useContext, useState, useEffect } from "react";
import "./Welcome.css";
import Divider from "@mui/material/Divider";
import { Link } from "react-router-dom";

import socketClient from "socket.io-client";

const SERVER = "http://localhost:8000/";

function Card({ title, amount }) {
  return (
    <div className="card">
      <h2>{`${title}  ${amount}`}</h2>
    </div>
  );
}

function SignIn(props) {
  const [userData, setUserData] = useState({
    username: "",
    password: "",
  });
  const [errorMessage, setErrorMessage] = useState("");
  // const navigate = useNavigate();

  function handleChange(e) {
    const { name, value } = e.target;
    setUserData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
    // console.log(userData);
  }

  function handleSubmit(e) {
    e.preventDefault();
    console.log("submitted" + userData.username + userData.password);
    console.log(localStorage.getItem("isAuthenticated"));
    if (userData.username === "" || userData.password === "") {
      setErrorMessage((prevState) => ({
        value: "Empty username/password field",
      }));
    } else if (
      userData.username.toLowerCase() === "admin" &&
      userData.password === "123456"
    ) {
      //Signin Success
      localStorage.setItem("isAuthenticated", "true");
      window.location.pathname = "/screen=0";
      console.log(localStorage.getItem("isAuthenticated") + "SIGNIN PAGE");
      // navigate("/screen=0");
    } else {
      //If credentials entered is invalid
      setErrorMessage((prevState) => ({ value: "Invalid username/password" }));
      return;
    }
  }
  return (
    <div>
      <form>
        <h1>Sign-In</h1>
        <input
          name="username"
          placeholder="username"
          onChange={handleChange}
        ></input>
        <input
          name="password"
          placeholder="password"
          onChange={handleChange}
        ></input>
        <button onClick={handleSubmit}>Submit</button>
      </form>
    </div>
  );
}

function Welcome(props) {
  var socket = socketClient(SERVER);
  const [amount, setAmount] = useState(0);
  const [array, setArray] = useState([]);
  const [userData, setUserData] = useState({
    username: "",
    password: "",
  });
  const [isAdmin, setIsAdmin] = useState(false);

  // useEffect(() => {
  //   localStorage.setItem("isAuthenticated", false);
  //   console.log(localStorage.getItem("isAuthenticated"));
  // }, []);

  useEffect(() => {
    socket.on("stats", (amount, array) => {
      // console.log(stats);
      setAmount(amount);
      setArray(array);
      // array.map((a) => {
      //   // console.log(a);
      //   let b = a.substring(a.indexOf("="));
      //   let c = b.substring(1);
      //   // console.log(c);
      //   a = c;
      // });

      // array.forEach((screen) => {
      //   screen.substring(screen.indexOf("="));
      // });
      // console.log(array);
    });
  }, [amount]);

  const clients = array.map((a, index) => (
    <Link to={a} key={index}>
      <Card key={index} title={a.slice(a.indexOf("=") + 1)} amount={""}>
        {a}
      </Card>
    </Link>
  ));

  // return (
  //   <div className="container">
  //     <div className="welcome__header">
  //       <h1>Welcome to our Ads-System Admin Dashboard</h1>
  //       {/* <h4>Use the navigation bar above to start navigate in app</h4> */}
  //     </div>
  //     <div className="app_descrip">
  //       <div className="capabilities">
  //         <h4>Application capabilities</h4>
  //         <p>➦ Viewing relevant ads from infinte amount of client-screens </p>
  //         <p>➦ RESTful api supported</p>
  //         <p>➦ All data is stored in MongoDB</p>

  //         <p>➦ Managing DB docs from server and client side</p>
  //         <p>➦ Login authentication for admin permissions</p>
  //       </div>
  //       <div className="how_to_use">
  //         <h4>How to use</h4>
  //         <p>
  //           ➦ To view ads client screens, Choose one of the available screens
  //           from 'Screens' dropdown at the navbar
  //         </p>
  //         <p>➦ To view all ads for admin use, Click 'All Ads' at the navbar</p>
  //         <p>➦ Edit/Delete ads is available from the All-Ads admin panel</p>
  //       </div>
  //     </div>
  //     <Divider />
  //     <h1 className="stats_h1">{`System Statics`}</h1>
  //     <div className="stats">
  //       <Card title={"Active Screens: "} amount={amount} />
  //       <Card title={"Screens connected: "} amount={array} />
  //     </div>
  //     <Divider />

  //     <footer>
  //       By Tomer Cohen, Guy Levy, Gili Shukrun, Shir Fogel and Omer Tati
  //     </footer>
  //   </div>
  // );

  if (isAdmin) {
    return (
      <div className="banner">
        <div className="banner__search">
          <h1>Welcome to our Ads-System Admin Dashboard</h1>
          <h1>{localStorage.getItem("auth")}</h1>
        </div>
        <div className="banner__info">
          <h2>Use the navigation bar above to start navigate in app</h2>
          <h1>Get out and strech your imagination</h1>
          <h5>
            Plan a different kind of gateway to uncover the hidden gems near
            you.
          </h5>
        </div>
        <div className="banner__stats">
          <p>{`Active Screens: ${amount}`}</p>
          <h2>Connected clients</h2>
          <div className="list">
            {clients.length == 0 ? "No active clients" : clients}
          </div>
        </div>
        <Divider />

        <footer>
          By Tomer Cohen, Guy Levy, Gili Shukrun, Shir Fogel and Omer Tati
        </footer>
      </div>
    );
  } else {
    return <SignIn />;
  }
}

export default Welcome;
