import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useHistory, useParams } from "react-router-dom";

function App() {
  const [data, setData] = useState(null);
  const [num, setNum] = useState(0);
  const [dataShown, setdataShown] = useState(null);

  useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);

  let relevantAds = [];
  const { id } = useParams();
  if (data) {
    relevantAds = data.filter((a) => a.screens.includes(id % 4));
  }

  let duration = 1000;
  useEffect(() => {
    let length = 0;
    setTimeout(() => {
      if (data) {
        length = relevantAds.length;
      } else {
        length = 3;
      }
      setNum((num + 1) % length);
      setdataShown(relevantAds[num]);
      duration += 500;
    }, duration);
  }, [num]);

  return (
    <>
      <div className="App">
        <header className="App-header">
          <h4>{`Screen #${id}`}</h4>
          <h1>{!dataShown ? "Loading ad title..." : `${dataShown.name}`} </h1>
          <p>
            {!dataShown ? "Loading description..." : `${dataShown.description}`}
          </p>

          <img
            className="photo"
            src={!dataShown ? null : `${dataShown.image}`}
          />
        </header>
      </div>
    </>
  );
}

export default App;
