import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

function ShowAllAds(props) {
  const [ads, setAds] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/api")
      .then((res) => {
        setAds(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  console.log(ads.message);

  return (
    <>
      <div>
        {!ads ? (
          "Loading..."
        ) : (
          <ul className="page">
            {ads.message.map((ad, index) => {
              <li>{ad.name}</li>;
            })}
          </ul>
        )}
      </div>
      <a href="/screen=0"> Back to Admin Page</a>
    </>
  );
}

export default ShowAllAds;
