import * as React from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";
import "./ShowAllAds.css";

export default function MediaCard({ ad }, handleDelete) {
  const { _id, name, description, image, screens } = ad;

  return (
    <Card className="card" sx={{ maxWidth: 345 }}>
      <CardMedia component="img" height="140" image={image} alt="ad image" />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {name}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {description}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {`Presented on screens: ${screens}`}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Edit</Button>
        <Button
          size="small"
          name={_id}
          title={name}
          onClick={() => console.log("CLICK")}
        >
          Delete
        </Button>
      </CardActions>
    </Card>
  );
}
