import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import "./AllAds.css";

function AdCard({ ad }) {
  // return <li key={ad._id}>{ad.name + "   On Screens: " + ad.screens}</li>;
  return (
    <div className="ad-card">
      <h3>{`Ad Title: ${ad.name}`}</h3>
      <h4>{`Presented on screens: ${ad.screens}`}</h4>
      <button>Edit</button>
    </div>
  );
}

function Home(props) {
  const [data, setData] = useState(null);
  var listAds;
  if (data) {
    listAds = data.map((ad) => <AdCard ad={ad} />);
    console.log(data);
  }
  useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);
  return (
    <>
      <div>{!data ? "Loading..." : <div className="page">{listAds}</div>}</div>
      <a href="/screen=0"> Back to Admin Page</a>
    </>
  );
}

export default Home;
