import logo from "./logo.svg";
import "./App.css";
import { useEffect, useState } from "react";

function App() {
  const [data, setData] = useState(null);
  const [num, setNum] = useState(0);
  const [imageShown, setImageShown] = useState(null);
  const [id, setId] = useState(null);

  const nums = [1, 2, 3, 4, 5];

  useEffect(() => {
    fetch("/screen=:id")
      .then((res) => res.json())
      .then((data) => (setData(data.message), setId(data.id)));
  }, []);

  // useEffect(() => {
  //   setTimeout(() => {
  //     setNum((num + 1) % 3);
  //     setData(data[num]);
  //     console.log(data);
  //   }, 3000);
  // }, [num]);

  useEffect(() => {
    let length = 0;
    setTimeout(() => {
      console.log(data);
      if (data) {
        length = data.length;
        console.log(length);
      } else {
        length = 3;
      }
      setNum((num + 1) % length);
      setImageShown(data[num]);
    }, 3000);
  }, [num]);

  return (
    <>
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1>{!imageShown ? "Loading text..." : `${imageShown.name}`} </h1>
          <p>{!imageShown ? "Loading text..." : `${imageShown.description}`}</p>

          <img src={!imageShown ? null : `${imageShown.image}`} />
        </header>
      </div>

      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/about">About</Link>
              </li>
              <li>
                <Link to="/users">Users</Link>
              </li>
            </ul>
          </nav>

          <Switch>
            <Route path="/about">
              <About />
            </Route>
            <Route path="/users">
              <Users />
            </Route>
            <Route path="/">
              <Home />
            </Route>
          </Switch>
        </div>
      </Router>
    </>
  );
}

export default App;
