import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import { BrowserRouter, Route, Routes, Link } from "react-router-dom";
import "./ShowAllAds.css";
import { useParams } from "react-router-dom";
import MediaCard from "./MediaCard";
import Button from "@mui/material/Button";

function AdCard({ ad }) {
  const { _id, name, description, image, screens } = ad;
  const params = useParams();

  return (
    <div className="card" key={_id}>
      <div className="name-description">
        <h3>{name}</h3>
        <p>{description}</p>
        <p>{`Presented on Screens: ${screens}`}</p>
        <img src={image} width="100px" />
      </div>

      <div className="button-container">
        <a href={`/edit-ad/${_id}`}>
          <button>Edit</button>
        </a>
        <button name={_id} title={name} onClick={handleDelete}>
          Delete
        </button>
      </div>
    </div>
  );
}

function ShowAllAds() {
  const [ads, setAds] = useState([]);
  useEffect(() => {
    axios
      .get("http://localhost:8000/api/ad")
      .then((res) => {
        setAds(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);

  let list = ads.map((ad) => {
    <div>{ad.name}</div>;
  });
  console.log(ads);

  return (
    <section className="container">
      <section className="contents">
        <h1>ADS:</h1>
        <Link to="/screen=0">
          <button>Back To Admin Screen</button>
        </Link>

        <div className="page">
          {ads.map((ad, index) => (
            <MediaCard handleDelete={handleDelete} key={index} ad={ad} />
          ))}
        </div>
      </section>
    </section>
  );
}

export default ShowAllAds;
