import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import "./ShowAllAds.css";

function AdCard({ ad }) {
  return (
    <div className="ad-card">
      <h3>{`Ad Title: ${ad.name}`}</h3>
      <h4>{`Presented on screens: ${ad.screens}`}</h4>
      <button>Edit</button>
    </div>
  );
}

function ShowAllAds(props) {
  const [ads, setAds] = useState([]);
  var listAds;

  useEffect(() => {
    axios
      .get("http://localhost:3001/api")
      .then((res) => {
        setAds(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
    // if (ads.message) {
    //   listAds = ads.message.map((ad, index) => <AdCard key={index} ad={ad} />);
    //   console.log(listAds);
    // }
  }, []);

  //   console.log(ads.message, listAds);

  return (
    <>
      {/* <div className="col">
        <h1>Mi Casa</h1>
        <p>This is my house y&apos;all!</p>
        {ads.message.map((home) => (
          <div>{home.name}</div>
        ))}
      </div>{" "} */}
      <div className="col">
        <h1>
          {!ads.message
            ? " Loading amount..."
            : `Number of Ads: ${ads.message.length}`}
        </h1>

        {/* {!ads.message ? "Loading.." : <div className="page"> {listAds}</div>} */}
        <div>
          {!ads.message ? (
            "Loading.."
          ) : (
            <div>
              {ads.message.map((ad, index) => {
                <AdCard ad={ad} />;
              })}
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default ShowAllAds;
